import { createAsyncThunk } from "@reduxjs/toolkit";
import { API_KEY } from "../weatherReducer";

export const fetchForecast = createAsyncThunk('ForecastWeather/ForecastReducer',(location) => {
    return fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&appid=${API_KEY}`)
            .then(data => data.json())

})
