import React, { useEffect, useState } from 'react'
const API_KEY = 'a43370d07c332057a138f54300a5a38a'

export default function MainBlock() {
    const [location, setLocation] = useState('')
    function findLocation(){
        fetch(`api.openweathermap.org/data/2.5/forecast/daily?q=${location}&units=metric&cnt=7&appid=${API_KEY}`)
    }

  return (
    <div className='current-day'>
        <form>
            <label>LOCATION<input type="text" value={''} onChange={e=> setLocation(e.target.value)}/></label>
            <button type='submit' onSubmit={findLocation()}>SEARCH</button>
        </form>
    </div>
  )
}
