import { API_KEY, isFetchError } from "../weatherReducer";
import { getForecastWeather } from "../weatherReducer";

export const fetchForecast = (location) => {
    return async function(dispatch){
        try{
            fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&appid=${API_KEY}`)
            .then(data => data.json())
            .then(data => dispatch(getForecastWeather(data)))
        }
        catch(err){
            dispatch(isFetchError(true))
        }

    }
}
