import React, { useLayoutEffect, useState } from 'react'
import { faWater } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLocationDot } from '@fortawesome/free-solid-svg-icons'
import { faCalendar } from '@fortawesome/free-solid-svg-icons'


export default function CurrentWeather() {
  const [day, setDay] = useState('')
  const [city, setCity] = useState('')
  useLayoutEffect(() => {
    let data = new Date();
    setDay(data.getDay());

  })
  return (
    <div className='current'>
      <div className='temerature-block'>
        <h2>Now</h2>
        <div className="temperature">-20<sup>o<sub>c</sub></sup>
        <FontAwesomeIcon icon={faWater} />
        </div>
        <p>Fog</p>
      </div>
      <div>
        <div><FontAwesomeIcon icon={faLocationDot} /><p className='day-week'>{}</p></div>
        <div><FontAwesomeIcon icon={faCalendar} /><p className='location'></p></div>
      </div>
    </div>
  )
}
