import React, { useEffect } from 'react'
import { images } from './App'
import { useSelector } from 'react-redux';

export default function ForecastWeather() {
  const weather = useSelector(state => state.weather.forecast)
  let options2 = { month: "long" };
  let weatherArr = []
  if(weather.length > 0){
    let prevDay
    for(let i = 0; i < weather.length; i++){
      const date = new Date(weather[i].dt_txt)
      let day = date.getDate()
      console.log(weather[i])
      if(day - prevDay === 1 || prevDay === undefined){
        let time = new Intl.DateTimeFormat("en-US", options2).format(date)
        weatherArr.push({...weather[i], day: day, time: time})
      }
      prevDay = day
    }
  }
  

  return (
    <div className='forecast'>
      <h2>5 Days Forecast</h2>
        <div className="current days">
            {weatherArr.map((el, idx) => 
              <div className='day' key={idx}>
                <div class="temp">{Math.floor(el.main.temp)}<sup>o<sub>c</sub></sup></div>
                <img class="weather" src={images[el.weather[0].main]}></img>
                <div class='time'>{el.day} {el.time}</div>
              </div>
            )}
        </div>
    </div>
  )
}
