import { API_KEY } from ".";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const getHourlyWeatherFunc = createAsyncThunk('HourlyWeather/fetchHourluWeather', async(location) => {
    const res = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&cnt=9&appid=${API_KEY}`)
    
})

const hourlyWeather = createSlice({
    name: 'hourly',
    initialState: {
        loading: false,
        hourly: [],
        error: ''
    },
    extraReducers: (builder) => {
        builder.addCase(getHourlyWeatherFunc.pending, (state) => {
            state.loading = true
        })
        builder.addCase(getHourlyWeatherFunc.rejected, (state) => {
            state.loading = false
            state.hourly = []
            state.error = 'error'
        })
        builder.addCase(getHourlyWeatherFunc.fulfilled, (state, action) => {
            state.loading = false
            state.hourly = [...action.payload.list]
            state.error = ''
        })
    }
})

export default hourlyWeather.reducer
