import React, { useEffect, useLayoutEffect } from 'react'
import { images } from './App'
import { API_KEY } from './App'

export default function HourlyWeather({location}) {
  let items = []
  useEffect(() => {
    fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&cnt=9&appid=${API_KEY}`)
    .then(data => data.json()).then(data => {
      for(let i = 0; i < data.list.length; i++){
        const date = new Date(data.list[i].dt_txt)
        let item = {
          temp: Math.floor(data.list[i].main.temp),
          weather: images[data.list[i].weather[0].main],
          date: date.toLocaleTimeString('en-US')
        }
        items.push(item)
      }
    })
    const block = document.querySelector('.hours__block')
    for(let i = 0; i < items.length; i++){
      block.innerHTML += `
        <div class='hour'>
          <div class='time'>${items[i].date}</div>
          <img className="weather" src=${items[i].weather}></img>
          <div className="temp">${items[i].temp}</div>
        </div>
      `
    }
  }, location)
  console.log(location)
  return (
    <div className='hourly'>
        <h2>Today At</h2>
        <div className="hours__block">
        </div>
    </div>
  )
}
