import React, { useEffect, useState } from 'react'

export default function MainBlock() {
    const API_KEY = 'a43370d07c332057a138f54300a5a38a'
    const [location, setLocation] = useState('')
    function findLocation(){
        console.log(location)
        fetch(`api.openweathermap.org/data/2.5/forecast/daily?q=${location}&units=metric&cnt=7&appid=${API_KEY}`)
        .then(data => data.json()).then(console.log)
    }

  return (
    <div className='current-day'>
        <form>
            <label>LOCATION<input type="text" onChange={e => setLocation(e.target.value)}/></label>
            <button type='button' onClick={findLocation}>SEARCH</button>
        </form>
    </div>
  )
}
