import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { getCurWeatherFunc } from "./CurrentWeather/fetchCurrWeather";
import { getHourlyWeatherFunc } from "./HourlyWeather/fetchHourluWeather";
import { API_KEY } from ".";

const weatherReducer = createSlice({
    name: 'weather',
    initialState: {
        loading: false,
        weather: [],
        hourly: [],
        error: ''
    },

    extraReducers: (builder) => {
        builder.addCase(getCurWeatherFunc.pending, (state) => {
            state.loading = true
        })
        builder.addCase(getCurWeatherFunc.fulfilled, (state, action) => {
            state.loading = false
            state.weather = [{...action.payload}]
            state.error = ''
        })
        builder.addCase(getCurWeatherFunc.rejected, (state, action) => {
            state.loading = false
            state.weather = []
            state.error = action.error.message
        })


        

        builder.addCase(getHourlyWeatherFunc.pending, (state) => {
            state.loading = true
        })
        builder.addCase(getHourlyWeatherFunc.fulfilled, (state, action) => {
            state.loading = false
            state.hourly = [...action.payload.list]
            state.error = ''
        })
        builder.addCase(getHourlyWeatherFunc.rejected, (state, action) => {
            state.loading = false
            state.hourly = []
            state.error = action.error.message
        })
    }
})

export default weatherReducer.reducer
