  let prevDay
  return (
    <div className='forecast'>
      <h2>5 Days Forecast</h2>
        <div className="current days">
            {weather.map((el, idx) => {
              if(idx % 6 == 0){
                const date = new Date(el.dt_txt)
                let time = new Intl.DateTimeFormat("en-US", options2).format(date)
                let day = date.getDate()
                if(day - prevDay === 1){
                  <div class='day'>
                    <div class="temp">${Math.floor(el.main.temp)}<sup>o<sub>c</sub></sup></div>
                    <img class="weather" src={images[el.weather[0].main]}></img>
                    <div class='time'>{day} {time}</div>
                  </div>
                }
                prevDay = day
              }
          })}
        </div>
    </div>
  )
}
