import React, { useEffect, useState } from 'react'
import NavBlock from './NavBlock'
import CurrentWeather from './CurrentWeather'
import clouds from './img/cloud.png'
import rain from './img/rain.png'
import clear from './img/sun.png'
import mist from './img/mist.png'
import snow from './img/snow.png'

const API_KEY = 'a43370d07c332057a138f54300a5a38a'
const images = {
    'Clouds': clouds,
    'Haze': mist,
    'Mist': mist,
    'Clear': clear,
    'Rain': rain,
    'Snow': snow,
}

export default function App() {
  const [location, setLocation] = useState('')
  const [temp, setTemp] = useState(0)
  const [windDeg, setWinDeg] = useState(0)
  const [windSpeed, setSpeed] = useState(0)
  const [windGust, setGust] = useState(0)
  const [weatherName, setWeatherName] = useState('')
  const [, setWeather] = useState('')
  const [pressure, setPressure] = useState(0)
  const [humidity, setHumidity] = useState(0)
  const [visibility, setVisibility] = useState(0)
  const [feelsLike, setFeelsLike] = useState(0)
  let sunrise, sunset
  if(location !== ''){
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${API_KEY}`)
    .then(data=> data.json())
    .then(data => {
      setTemp(Math.floor(data.main.temp))
      setWeather(data.weather[0].main)
      setSpeed(data.wind.speed)
      setWinDeg(data.wind.deg)
      setGust(data.wind.gust)
      setHumidity(data.main.humidity)
      setVisibility(data.visibility / 1000)
      setPressure(data.main.pressure)
      setFeelsLike(data.main.feels_like)
      console.log(data)
    })
    .catch(() => console.log('404'))
  }


  return (
    <div className='wrapper'>
      <nav>
        <NavBlock setLocation={setLocation}/>
      </nav>
      <main>
        <CurrentWeather location={location} temp={temp} weather={images[weather]}/>
      </main>
    </div>
  )
}
