import React, { useLayoutEffect, useState } from 'react'
import { faWater } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLocationDot } from '@fortawesome/free-solid-svg-icons'
import { faCalendar } from '@fortawesome/free-solid-svg-icons'


export default function CurrentWeather({city}) {
  const [day, setDay] = useState('')
  const [date, setDate] = useState('')
  const [month, setMonth] = useState('')
  useLayoutEffect(() => {
    let data = new Date();
    setDay(data.getDay());
    switch(day){
      case 1: setDay('Monday')
      break;
      case 1: setDay('Monday')
      break;
      case 1: setDay('Monday')
      break;
      case 1: setDay('Monday')
      break;
      case 1: setDay('Monday')
      break;
      case 6: setDay('Monday')
      break;
      case 7: setDay('Monday')
      break;
      default: break;
    }
    setDate(data.getDate());
    setMonth(data.getMonth() + 1);
  })


  console.log(month + 1)
  return (
    <div className='current'>
      <div className='temerature-block'>
        <h2>Now</h2>
        <div className="temperature">-20<sup>o<sub>c</sub></sup>
        <FontAwesomeIcon icon={faWater} />
        </div>
        <p>Fog</p>
      </div>
      <div>
        <div><FontAwesomeIcon icon={faLocationDot} /><p className='day-week'>{day} {date}, {month}</p></div>
        <div><FontAwesomeIcon icon={faCalendar} /><p className='location'>{city}</p></div>
      </div>
    </div>
  )
}
