import React, { useLayoutEffect, useState } from 'react'
import { faWater } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLocationDot } from '@fortawesome/free-solid-svg-icons'
import { faCalendar } from '@fortawesome/free-solid-svg-icons'


export default function CurrentWeather({location}) {
  const [day, setDay] = useState('')
  const [date, setDate] = useState('')
  const [month, setMonth] = useState('')
  useLayoutEffect(() => {
    let options = { weekday: "long" };
    let options2 = { month: "long" };
    let data = new Date();
    setDate(data.getDate());
    setDay(new Intl.DateTimeFormat("en-US", options).format(date));
    setMonth(new Intl.DateTimeFormat("en-US", options2).format(date).slice(0, 3));
  })

  return (
    <div className='current'>
      <div className='temerature-block'>
        <h2>Now</h2>
        <div className="temperature">{temp}<sup>o<sub>c</sub></sup>
        <FontAwesomeIcon icon={faWater} />
        </div>
        <p>Fog</p>
      </div>
      <div>
        <div><FontAwesomeIcon icon={faLocationDot} /><p className='day-week'>{day} {date}, {month}</p></div>
        <div><FontAwesomeIcon icon={faCalendar} /><p className='location'>{location}</p></div>
      </div>
    </div>
  )
}
