import { API_KEY } from "../weatherReducer";
import { getCurrWeather } from "../weatherReducer";
import { error } from "../App";

export const getCurWeatherFunc = (location) => {
    return function(dispatch) {
        try{
            fetch(`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${API_KEY}`)
            .then(data => data.json())
            .then(data => dispatch(getCurrWeather(data)))
        }catch(err){
            dispatch(isFetchError(true))

        };
    }
}
