import { createAsyncThunk } from "@reduxjs/toolkit";
import { API_KEY } from "../weatherReducer";

export const getHourlyWeatherFunc = createAsyncThunk('HourlyWeather/fetch',(location) => {
    return fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&cnt=9&appid=${API_KEY}`)
            .then(data => data.json())
})
