import { API_KEY } from "../weatherReducer";
import { isFetchError } from "../weatherReducer";

export const getHourlyWeatherFunc = (location) => {
    return async function(dispatch){
        try{
            fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&cnt=9&appid=${API_KEY}`)
            .then(data => data.json())
            .then(data => dispatch(getHourlyWeather(data)))
        }catch(err){
            dispatch(isFetchError(true))
        }
    }
}
