import { API_KEY } from ".";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";


export const fetchForecast = createAsyncThunk('ForecastWReducer/fetchForecast', (location) => {
    return fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&appid=${API_KEY}`)
            .then(data => data.json())
})

const ForecastWReducer = createSlice({
    name: 'forecast',
    initialState: {
        loading: false,
        weather: [],
        error: ''
    },
    extraReducers: (builder) => {
        builder.addCase(fetchForecast.pending, (state) => {
            state.loading = true
        })
        builder.addCase(fetchForecast.fulfilled, (state, action) => {
            state.loading = false
            state.forecast = [...action.payload.list]
            state.error = ''
        })
        builder.addCase(fetchForecast.rejected, (state, action) => {
            state.loading = false
            state.forecast = []
            state.error = action.error.message
        })
    }
})

export default weatherForecast.reducer
