import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { API_KEY } from ".";

export const getCurWeatherFunc = createAsyncThunk('CurrentWeather/getCurWeatherFunc', (location) => {
    return fetch(`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=${API_KEY}`)
        .then(data => data.json())
        .catch(err => console.log(err))

})

const weatherReducer = createSlice({
    name: 'weather',
    initialState: {
        loading: false,
        weather: [],
    },

    extraReducers: {
        [getCurWeatherFunc.pending]: (state) => {
            state.loading = true
        },
        [getCurWeatherFunc.rejected]: (state) => {
            state.loading = false
        },
        [getCurWeatherFunc.fulfilled]: (state, action) => {
            state.loading = false
            state.weather = [{...action.payload}]
        }
    }
})

export default weatherReducer.reducer
