import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { getCurWeatherFunc } from "./CurrentWeather/fetchCurrWeather";
export const API_KEY = 'a43370d07c332057a138f54300a5a38a'

const weatherReducer = createSlice({
    name: 'weather',
    initialState: {
        loading: false,
        weather: [],
        forecast: [],
        hourly: [],
        error: ''
    },
    reducers:{
        getCurrWeather (state, action){
            state.weather = [{...action.payload}] 
        },
        getForecastWeather (state, action) {
            state.forecast = [...action.payload.list]
        },
        getHourlyWeather (state, action) {
            state.hourly = [...action.payload.list]
        },
        isFetchError (state, action) {
            state.isError = action.payload;
        }
    },
    extraReducers: (builder) => {
        builder.addCase(getCurWeatherFunc.pending, (state) => {
            state.loading = true
        })
        builder.addCase(getCurWeatherFunc.fulfilled, (state, action) => {
            state.loading = false
            state.weather = [{...action.payload}],
            state.error = ''
        })
        builder.addCase(getCurWeatherFunc.rejected, (state))
    }
})

export default weatherReducer.reducer
export const {getCurrWeather, getForecastWeather, getHourlyWeather, isFetchError} = weatherReducer.actions
