import React from 'react'
import { API_KEY } from './App'
import { images } from './App'

export default function ForecastWeather({location}) {

  if(location !== '') getData()

    const getData = async () => {
      let url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${API_KEY}`;
      try {
        let res = await fetch(url);
        let data = await res.json();
        let lat = data.coord.lat;
        let lon = data.coord.lon;
        getDatafor7days(lat, lon);
      } catch (error) {
        console.log(error);
      }
    };

    const getDatafor7days = async (lat, lon) => {
      let url = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${API_KEY}`;
      try {
        let res = await fetch(url);
        let data = await res.json();
        console.log(data);
      } catch (error) {
        console.log(error);
      }
    };



