import React from 'react'

export default function HourlyWeather({items}) {
  console.log(items)
  const times = document.querySelectorAll('.time')
  times.forEach(time => {
    let text = time.textContent
    text = (time.textContent).replaceAll('0', '').replaceAll(':', '')
  })
  return (
    <div className='hourly'>
        <h2>Today At</h2>
        <div className="hours__block">
          {items.map((el, idx) => (
            <div key={idx} className='hour'>
                <div className="time">{el.date}</div>
                <div className="weather">{el.weather}</div>
                <div className="temp">{el.temp}</div>
            </div>
          ))}
        </div>
    </div>
  )
}
