import React, { useEffect } from 'react'
import { images } from '../App'
import { API_KEY } from '../weatherReducer'
import { useSelector } from 'react-redux'

export default function HourlyWeather({location}) {
  const block = document.querySelector('.hours__block')
  const weather = useSelector(state => state.weather.forecast)
  const hourlyWeather = []
  if(weather.length > 0){
    let options2 = { month: "long" };
    let prevDay = (new Date(weather[i].dt_txt)).getDate()
    for(let i = 0; i < weather.length; i++){
      const date = new Date(weather[i].dt_txt)
      let time = new Intl.DateTimeFormat("en-US", options2).format(date)
      let day = date.getDate()
      if(day == prevDay){
        hourlyWeather.push({...weather[i], time: time})
        prevDay = day
      }
    }
  }
        

  return (
    <div className='hourly'>
        <h2>Today At</h2>
        <div className="hours__block">
          {hourlyWeather.map((el, idx) => 
            <div class='hour' key={idx}>
              <div class='time'>{el.time}</div>
              <img class="weather" src={images[el.weather[0].main]}></img>
              <div class="temp">{Math.floor(el.main.temp)}<sup>o<sub>c</sub></sup></div>
            </div>
          )}
        </div>
    </div>
  )
}
