import { createSlice } from "@reduxjs/toolkit";

export const API_KEY = 'a43370d07c332057a138f54300a5a38a'

const weatherReducer = createSlice({
    name: 'weather',
    initialState: {
        weather: [],
        forecast: [],
        hourly: [],
        isError: false
    },
    reducers:{
        getCurrWeather (state, action){
            state.weather = [{...action.payload}] 
        },
        getForecastWeather (state, action) {
            state.forecast = [...action.payload.list]
        },
        getHourlyWeather (state, action) {
            state.hourly = [...action.payload.list]
        },
        isFetchError (state) {
            state.isError = true;
        }
    }
})

export default weatherReducer.reducer
export const {getCurrWeather, getForecastWeather, getHourlyWeather, isFetchError} = weatherReducer.actions
