import React, { useRef, useState } from 'react'
import clouds from './img/cloud.png'
import rain from './img/rain.png'
import clear from './img/sun.png'
import mist from './img/mist.png'
import snow from './img/snow.png'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons' 

const API_KEY = 'a43370d07c332057a138f54300a5a38a'
const images = {
    'Clouds': clouds,
    'Haze': mist,
    'Mist': mist,
    'Clear': clear,
    'Rain': rain,
    'Snow': snow,
}

export default function MainBlock({setLocation}) {
  const locRef = useRef(null)
    const handleSubmit = (e) => {
      e.preventDefault()
      return setLocation(locRef.current.value)
    }

  return (
    <div className='current-day'>
        <input ref={locRef} type="text" placeholder='Type Any Location...'/>
        <button type='button' onClick={handleSubmit}><FontAwesomeIcon icon={faMagnifyingGlass} />SEARCH</button>
    </div>
  )
}
