import React from 'react'
import { API_KEY } from './App'
import { images } from './App'

export default function ForecastWeather({location}) {
  const days = document.querySelector('.days')
  let options2 = { month: "long" };

    const getData = async() => {
      let data = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&units=metric&appid=${API_KEY}`)
      data = await data.json()

      for(let i = 0; i < data.list.length; i++){
        if(i % 6 == 0){
          console.log(data.list[i])
          const date = new Date(data.list[i].dt_txt)
          let time = new Intl.DateTimeFormat("en-US", options2).format(date).slice(0, 3)
          days.innerHTML += `
            <div class='day'>
              <div class="temp">${Math.floor(data.list[i].main.temp)}<sup>o<sub>c</sub></sup></div>
              <img class="weather" src=${images[data.list[i].weather[0].main]}></img>
              <div class='time'>${date.getDate()}${time}</div>
            </div>
          `
        }else continue
      }
    }
    
    if(location !== '') getData()
    
  return (
    <div>
      <div className="days">

      </div>
    </div>
  )
}
